package week1.Lab1;

public class Q2_UnaryOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int length =2, breadth=3;
		
		//evaluate arithmetic expression using unary Lab2
		System.out.println( "using post increment " + length++);
		System.out.println( "using post increment " + ++length);

		System.out.println( "using pre decrement " + --breadth);
		System.out.println( "using post decrement " + breadth--);
	}

}
