package week1.Lab1;

public class Q3_QuotientRemainder {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int q;
		int rem;
		int num1 = 5;
		int num2 = 2;
		
		rem = num1%num2;
		q = num1/num2;
		
		System.out.println("Remainder is: "+ rem);
		System.out.println("Quotient is: "+ q);
	}

}
