package week1.day1.assignments;

public class Assignment_Table {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i = 2;
		for(int j =1; j<=10; j++ ) {					
			System.out.println(i + "*" + j + "=" + i*j);
		}
		
		//method 2
		for(int k =1; k<=10; k++ ) {					
			System.out.println("2" + "*" + k + "=" + k*2);
		}
	}

}
